	(430,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'天下第一湾','xhtml','亚龙湾国家旅游度假区是中华人民共和国海南省三亚市东郊的一处优质热带海滨风景区，距离市中心区约10公里。亚龙湾为一个月牙湾，拥有7千米长的银白色海滩，沙质相当细腻。而这里的南海没有受到污染，海水洁净透明，远望呈现几种不同的蓝色，而水面下珊瑚种类丰富，可清楚观赏珊瑚，适合多种水面下活动包括潜水等，令海底成为了当地的旅游的核心。岸上林木郁郁葱葱。冬季这里的气温27摄氏度，水温20摄氏度，是一处理想的冬季避寒和休闲度假胜地。号称“东方夏威夷”。沿海岸一带布满多座高级酒店，包括国际五星级的假日酒店。','xhtml','{filedir_48}ya_long_wan.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(431,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'盐田万顷莺歌海、四季常春极乐园。','xhtml','这里是东经108.73度，北纬18.51度，海南岛西南端的一个突起的犄角。它有一个极为动人的名字，却很少被人提及。在旅游成为海南唯一话题的时候，它更是退缩到默默无闻。\n\n浩淼的大海，似乎总与浪漫相连，总能让人产生奇妙的联想。不知是远古时精卫鸟填海的壮举感动了大地，在谈笑间把一个富有诗意的名字赋予了她，将她唤作莺歌海。','xhtml','{filedir_48}ying_ge_hai.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(432,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'热带雨林资源博览馆','xhtml','呀诺达热带雨林景区是地处北纬18度的真正热带雨林，是海南岛五大热带雨林精品的浓缩，是热带雨林资源博览馆。\n\n呀诺达景区距三亚市仅35公里，距凤凰机场52公里，是名符其实的三亚后花园。景区北与五指山、七仙岭比肩相连；东眺南海万倾波涛，美丽的海棠湾近在咫尺与南中国第一温泉南田温泉仅一水之隔。在三亚市最新修编的旅游总体规划中，本景区位于大三亚旅游规划中的生态景观轴上，是“三亚旅游圈”的“金三角”地区。','xhtml','{filedir_48}ya_nuo_da.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(433,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'烟波浩渺，海天一色。','xhtml','岛东、南、西三面漫山叠翠，85科2700多种原生植物郁郁葱葱，不但有高大挺拔的乔木，也有繁茂葳蕤的灌木,其中不但有从恐龙时代流传下来的沙椤这样的奇异花木，还生长着迄今为止地球上留存下来最古老的植物，号称“地球植物老寿星”的龙血树，寄生、绞杀等热带植 物景观随处可见。\n\n临海山石嶙峋陡峭，直插海底，惊涛拍岸，蔚为壮观。中部山林草地起伏逶迤，绿影婆娑。北部滩平浪静，沙质洁白细腻，恍若玉带天成。\n\n四周海域清澈透明，海水能见度6—27米，水域中盛产夜光螺、海参、龙虾、马鲛鱼、海胆、鲳鱼及五颜六色的热带鱼，南部水域海底有着保护很好的珊瑚礁，是世界上为数不多的惟一没有礁石或者鹅卵石混杂的海岛，是国内最佳潜水基地。极目远眺，烟波浩渺，海天一色。','xhtml','{filedir_48}wu_zhi_zhou.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(434,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'翡翠山城','xhtml','五指山市位于海南岛中南部腹地，是海南岛中部地区的中心城市和交通枢纽，也是海南省中部少数民族的聚居地。\n\n五指山市是有名的“翡翠山城”，因海南岛上最高山峰五指山而得名，有五指山市热带雨林、海南民族博物馆、七指岭 、热带植物园、中华民族文化村、卧龙山、太平山瀑布、琼州学院 、海瑞祖居、琼崖公学纪念亭、鹦歌岭、甘什岭、民族博物馆和仿古黎村、白沙起义纪念馆、仙龙洞 、黎苗民族歌舞长廊、木色旅游度假风景区等风景名胜。','xhtml','{filedir_48}wu_zhi_shan.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(436,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'海南现存未被开发的最美丽的海湾。','xhtml','石梅湾位于海南万宁市兴隆华侨农场南部，距海南兴隆华侨旅游经济区12公里。东经110．5度，北纬18．5度，以石梅湾为中心30平方公里的范围分布有海南兴隆侨乡国家森林公园、兴隆温泉、兴隆热带花园、兴隆热带植物园、太阳河、神州半岛、大洲岛、日月湾、万宁首创奥特莱斯、兴隆国家绿道、永范花海、兴隆三角梅公园、杨梅湾等几个已开发和待开发的旅游区。海南东线光纤通讯系统远见划中石梅湾也被列为中心站。石梅湾的地理位置及交通、通讯的中枢作用确立了其作为海南省东南海岸线旅游中心区的位置。','xhtml','{filedir_48}shi_mei_wan.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(437,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'浑然天成棋子湾。','xhtml','棋子湾在海南岛西海岸区域，位于昌江西部，在昌化镇北3公里处，距昌江县城石碌镇55公里。此湾东倚昌化岭，西连大海，东西长约20公里，呈S状，石多沙白浪静。该湾水清见底，沙细质软，洁白如银。海岸奇峰林立，怪石磷峋多姿，林木苍翠，山花烂漫，清泉欢畅，阳光明丽，四季如春，是难得的天然浴场，也是光浴和沙浴的理想之地。怪石嶙峋，色彩各异，林木茂密，绿草如茵，清泉欢歌，银色的沙滩与湛蓝大海面连成一片，让人的心灵享受飞翔。','xhtml','{filedir_48}qi_zi_wan.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
	(438,27,77,0,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'',NULL,NULL,NULL,'',NULL,'',NULL,'',NULL,'',NULL,'',NULL,NULL,NULL,'最具特色的街道景观','xhtml','骑楼建筑最早见于两千多年前的古希腊，近代才流行于南欧及地中海一带，并传至东南亚。海口得胜沙路、文明东路、中山路、博爱路、解放路，两侧都是一幢幢始建于十九世纪末的南洋风格柱廊式骑楼。\n\n海口的骑楼老街建筑群初步形成于1920年代至1940年代，其中最古老的建筑四牌楼建于南宋，距今有600多年历史。骑楼的历史与海口早期的对外开放息息相关。晚清时期，海口是当时全国对外开放的口岸之一，全岛对外开放的窗口，在南洋谋生的人开始回家乡投资建设。1849年，最早的骑楼在水巷口、博爱北路一带的四牌楼街区建成，整片骑楼街区就是从这里延伸开来，形成了规模。','xhtml','{filedir_48}qi_lou_lao_jie.jpg','none','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','xhtml','','none','','none','','none','','none','','xhtml','','xhtml','','none','','none','','none','','xhtml','','none','','none','','xhtml','','none','','none','','none','','none','','none','','none','','none','','none','','none','','none','','xhtml','','xhtml'),
